import * as React from "react";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import Link from "@mui/material/Link";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";


const defaultTheme = createTheme();

export default function AddTour() {
  const [tourInfo, setTourInfo] = useState(null);
  const [tourPhoto, setTourPhoto] = useState(null);
  let navigate = useNavigate();

  const handleChange = (event) => {
    setTourInfo({ ...tourInfo, [event.target.name]: event.target.value });
  };

  const handleFileChange = (event) => {
    setTourPhoto(event.target.files[0]);
  };

  const handleSubmit = () => {
    const data = new FormData();
    data.append("title", tourInfo.title);
    data.append("city", tourInfo.city);
    data.append("address", tourInfo.address);
    data.append("photo", tourPhoto);
    data.append("desc", tourInfo.desc);
    data.append("price", tourInfo.price);
    data.append("maxGroupSize", tourInfo.maxGroupSize);

    axios
      .post("/api/Tour", data)
      .then((response) => {
        console.log(response);
        navigate("/"); // Redirect to home page after successful submission
      })
      .catch((error) => {
        console.error("Error adding tour:", error);
      });
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Add Tour
          </Typography>
          <Box
            component="form"
            onSubmit={handleSubmit}
            noValidate
            sx={{ mt: 3 }}
          >
            <TextField
              onChange={handleChange}
              margin="normal"
              required
              fullWidth
              id="title"
              label="Title"
              name="title"
              autoFocus
            />
            <TextField
              onChange={handleChange}
              margin="normal"
              required
              fullWidth
              id="city"
              label="City"
              name="city"
            />
            <TextField
              onChange={handleChange}
              margin="normal"
              required
              fullWidth
              id="address"
              label="Address"
              name="address"
            />
            <TextField
              onChange={handleFileChange}
              margin="normal"
              required
              fullWidth
              id="photo"
              name="photo"
              label="Photo"
              type="file"
            />
            <TextField
              onChange={handleChange}
              margin="normal"
              required
              fullWidth
              id="desc"
              label="Description"
              name="desc"
            />
            <TextField
              onChange={handleChange}
              margin="normal"
              required
              fullWidth
              id="price"
              label="Price"
              name="price"
              type="number"
            />
            <TextField
              onChange={handleChange}
              margin="normal"
              required
              fullWidth
              id="maxGroupSize"
              label="Max Group Size"
              name="maxGroupSize"
              type="number"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3 }}
            >
              Add Tour
            </Button>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
}
