import React, { createContext, useEffect, useReducer } from "react";

let user;
try {
  user = JSON.parse(localStorage.getItem("user"));
} catch (error) {
  console.error("Error parsing user data from localStorage:", error);
  user = null;
}

const initial_state = {
  user: user,
  authToken: localStorage.getItem("authToken") || null, // Add authToken to initial state
  loading: false,
  error: null,
};

export const AuthContext = createContext(initial_state);

const AuthReducer = (state, action) => {
  switch (action.type) {
    case "LOGIN_START":
      return {
        ...state,
        loading: true,
        error: null,
      };
    case "LOGIN_SUCCESS":
      localStorage.setItem("authToken", action.payload.authToken); // Set authToken in localStorage
      return {
        ...state,
        user: action.payload.user,
        authToken: action.payload.authToken,
        loading: false,
        error: null,
      };

    case "LOGIN_FAILURE":
      return {
        ...state,
        loading: false,
        error: action.payload,
      };

    case "REGISTER_SUCCESS":
      return {
        ...state,
        loading: false,
        error: null,
      };
    case "LOGOUT":
      localStorage.removeItem("user");
      localStorage.removeItem("authToken"); // Remove authToken from localStorage
      return {
        ...state,
        user: null,
        authToken: null,
        loading: false,
        error: null,
      };

    default:
      return state;
  }
};

export const AuthContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AuthReducer, initial_state);

  useEffect(() => {
    localStorage.setItem("user", JSON.stringify(state.user));
  }, [state.user]);

  useEffect(() => {
    if (state.user && state.user.status === "blocked") {
      localStorage.removeItem("user");
      localStorage.removeItem("authToken");
      dispatch({ type: "LOGOUT" });
    }
  }, [state.user]);

  return (
    <AuthContext.Provider
      value={{
        user: state.user,
        authToken: state.authToken, // Pass authToken in the context value
        loading: state.loading,
        error: state.error,
        dispatch,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
