const handleNotificationClick = async () => {
  try {
    const userId = user._id; // Assuming user ID is stored in user._id
    const response = await axios.get(`http://localhost:7000/api/booking/${userId}`);
    const responseData = response.data.data;
    if (Array.isArray(responseData)) {
      setNotificationMessage(responseData);
    } else {
      setNotificationMessage([responseData]); // Wrap non-array response in an array
    }
    toggleModal();
  } catch (error) {
    console.error("Error fetching notification:", error);
  }
};
