import React, { useState, useEffect } from "react";
import { Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import "./tour-card.css";
import calculationAvgRating from "../utils/avgRating";
import axios from "axios";

const TourCard = ({ tourId }) => {
  const [tour, setTour] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTour = async () => {
      try {
        const response = await axios.get(`http://localhost:7000/api/tour/get`);
        setTour(response.data.tour);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchTour();
  }, [to]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!tour) {
    return <div>No tour found</div>;
  }

  const { id, title, city, photo, price, featured, reviews } = tour;
  const { totalRating, avgRating } = calculationAvgRating(reviews);

  return (
    <div className="tour_card">
      <Card>
        <div className="tour_img">
          <img src={photo} alt="tour-img" />
          {featured && <span>Featured</span>}
        </div>

        <CardBody>
          <div className="card_top d-flex align-items-center justify-content-between">
            <span className="tour_loaction d-flex align-items-center gap-1">
              <i className="ri-map-pin-line"></i>
              {city}
            </span>
            <span className="tour_rating d-flex align-items-center gap-1">
              <i className="ri-star-fill"></i>
              {avgRating === 0 ? null : avgRating}
              {totalRating === 0 ? (
                "Not Rated"
              ) : (
                <span>({reviews.length})</span>
              )}
            </span>
          </div>
          <h5 className="tour_title">
            <Link to={`/tours/${id}`}>{title}</Link>
          </h5>
          <h5>
            <div className="card_button d-flex align-items-center justify-content-between mt-3">
              <h5>
                {price}Rs
                <span>/per person</span>
              </h5>
              <button className="btn booking_btn">
                <Link to={`/tours/${id}`}>Book Now</Link>
              </button>
            </div>
          </h5>
        </CardBody>
      </Card>
    </div>
  );
};

export default TourCard;
