import React, { useState, useEffect } from "react";
import { CommonSection } from "../shared/CommonSection";
import "../styles/tour.css";
import TourCard from "./../shared/TourCard";
import { Newsletter } from "./../shared/Newsletter";
import { Container, Row, Col, Button } from "reactstrap"; // Import Button from reactstrap
import { SearchBar } from "../shared/SearchBar";
import { useFetch } from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";

export default function Tours() {
  const [pageCount, setpagecount] = useState(0);
  const [page, setPage] = useState(0);
  const [filter, setFilter] = useState("All");
  const [featuredTours, setFeaturedTours] = useState([]);

  const {
    data: tours,
    loading,
    error,
  } = useFetch(`${BASE_URL}/tours/get?page=${page}`);
  const { data: tourCount } = useFetch(`${BASE_URL}/tours/search/getTourCount`);

  useEffect(() => {
    const pages = Math.ceil(tourCount / 8);
    setpagecount(pages);
    window.scrollTo(0, 0);
  }, [page, tourCount, tours]);

  const handleFilterChange = async (selectedFilter) => {
    setFilter(selectedFilter);
    setPage(0);
    if (selectedFilter === "Featured") {
      try {
        const res = await fetch(`${BASE_URL}/tours/search/Featured2`);
        if (!res.ok) {
          throw new Error("Failed to fetch featured tours");
        }
        const data = await res.json();
        setFeaturedTours(data);
      } catch (error) {
        console.error("Error fetching featured tours:", error);
      }
    }
  };

  return (
    <>
      <CommonSection title={filter === "All" ? "All Tours" : "Featured Tours"} />
      <section>
        <Container>
          <Row className="mb-3">
            <Col>
              <Button
                color={filter === "All" ? "primary" : "secondary"}
                onClick={() => handleFilterChange("All")}
              >
                All Tours
              </Button>
            </Col>
            <Col>
              <Button
                color={filter === "Featured" ? "primary" : "secondary"}
                onClick={() => handleFilterChange("Featured")}
              >
                Featured Tours
              </Button>
            </Col>
          </Row>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          {loading && <h4 className="text-center pt-5">Loading......</h4>}
          {error && <h4 className="text-center pt-5">{error}</h4>}
          {!loading && !error && (
            <Row>
              {filter === "All" ? (
                tours?.map((tour) => (
                  <Col lg="3" className="mb-4" key={tour._id}>
                    <TourCard tour={tour} />
                  </Col>
                ))
              ) : (
                featuredTours?.map((tour) => (
                  <Col lg="3" className="mb-4" key={tour._id}>
                    <TourCard tour={tour} />
                  </Col>
                ))
              )}
              <Col lg="12">
                <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                  {[...Array(pageCount).keys()].map((number) => (
                    <span
                      key={number}
                      onClick={() => setPage(number)}
                      className={page === number ? "active_page" : ""}
                    >
                      {number + 1}
                    </span>
                  ))}
                </div>
              </Col>
            </Row>
          )}
        </Container>
      </section>
      <Newsletter />
    </>
  );
}
