import React, { useState, useEffect } from "react";
import { CommonSection } from "../shared/CommonSection";
import { Container, Row, Col } from "reactstrap";
import { SearchBar } from "../shared/SearchBar";
import { TourCard } from "../shared/TourCard";
import { Newsletter } from "../shared/Newsletter";
import axios from "axios";

import "../styles/tour.css";

export default function Tours() {
  const [tours, setTours] = useState([]);
  const [pageCount, setPageCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    fetchTours();
  }, [currentPage]);

  const fetchTours = async () => {
    try {
      const response = await axios.get(`/api/tours?page=${currentPage}&limit=4`);
      const fetchedTours = response.data;
      setTours(fetchedTours);
      const totalPages = Math.ceil(fetchedTours.length / 4);
      setPageCount(totalPages);
    } catch (error) {
      console.error("Error fetching tours:", error);
    }
  };

  const handlePageClick = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <>
      <CommonSection title={"All Tours"} />
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          <Row>
            {tours.map((tour) => (
              <Col lg="3" className="mb-4" key={tour.id}>
                <TourCard tour={tour} />
              </Col>
            ))}
            <Col lg="12">
              <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                {[...Array(pageCount).keys()].map((pageNumber) => (
                  <span
                    key={pageNumber}
                    onClick={() => handlePageClick(pageNumber + 1)}
                    className={currentPage === pageNumber + 1 ? "active_page" : ""}
                  >
                    {pageNumber + 1}
                  </span>
                ))}
              </div>
            </Col>
          </Row>
        </Container>
      </section>
      <Newsletter />
    </>
  );
}
