// FeaturedTourList.jsx
import React, { useEffect, useState } from "react";
import TourCard from "../../shared/TourCard"; // Assuming the path is correct
import { Col } from "reactstrap";
import axios from "axios";

export const FeaturedTourList = () => {
  const [tourData, setTourData] = useState([]);

  useEffect(() => {
    // Fetch tour data from the server
    axios
      .get("http://localhost:7000/api/tour/get")
      .then((response) => {
        // Filter the tours to include only the featured ones
        const featuredTours = response.data.tours.filter((tour) => tour.featured);
        setTourData(featuredTours);
      })
      .catch((error) => {
        console.error("Error fetching tour data:", error);
      });
  }, []);

  return (
    <>
      {featuredTours.map((tour, index) => (
        <Col lg="3" className="mb-4" key={index}>
          <TourCard/>
        </Col>
      ))}
    </>
  );
};
