import React, { useState, useEffect } from "react";
import { CommonSection } from "../shared/CommonSection";
import "../styles/tour.css";
import TourCard from "./../shared/TourCard";
import { Newsletter } from "./../shared/Newsletter";
import { Container, Row, Col, Button } from "reactstrap"; // Import Button from reactstrap
import { SearchBar } from "../shared/SearchBar";
import { useFetch } from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";

export default function Tours() {
  const [pageCount, setpagecount] = useState(0);
  const [page, setPage] = useState(0);
  const [filter, setFilter] = useState("All");

  const {
    data: tours,
    loading: toursLoading,
    error: toursError,
  } = useFetch(`${BASE_URL}/tours/get?page=${page}`);

  const {
    data: tourCount,
    loading: tourCountLoading,
    error: tourCountError,
  } = useFetch(`${BASE_URL}/tours/search/getTourCount`);

  const {
    data: featuredTours,
    loading: featuredToursLoading,
    error: featuredToursError,
  } = useFetch(`${BASE_URL}/tours/search/Featured2`);

  useEffect(() => {
    if (tourCount) {
      const pages = Math.ceil(tourCount / 12);
      setpagecount(pages);
      window.scrollTo(0, 0);
    }
  }, [page, tourCount, tours]);

  const handleFilterChange = (selectedFilter) => {
    setFilter(selectedFilter);
    setPage(0);
  };

  return (
    <>
      <CommonSection
        title={filter === "All" ? "All Tours" : "Featured Tours"}
      />
      <section>
        <Container>
          <Row className="mb-3">
            <Col>
              <Button
                color={filter === "All" ? "primary" : "secondary"}
                onClick={() => handleFilterChange("All")}
              >
                All Tours
              </Button>
            </Col>
            <Col>
              <Button
                color={filter === "Featured" ? "primary" : "secondary"}
                onClick={() => handleFilterChange("Featured")}
              >
                Featured Tours
              </Button>
            </Col>
          </Row>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          {!toursLoading &&
            !tourCountLoading &&
            !featuredToursLoading &&
            !toursError &&
            !tourCountError &&
            !featuredToursError && (
              <Row>
                {filter === "All"
                  ? tours?.map((tour) => (
                      <Col lg="3" className="mb-4" key={tour._id}>
                        <TourCard tour={tour} />
                      </Col>
                    ))
                  : featuredTours?.map((tour) => (
                      <Col lg="3" className="mb-4" key={tour._id}>
                        <TourCard tour={tour} />
                      </Col>
                    ))}
                <Col lg="12">
                <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
  {!toursLoading &&
    !tourCountLoading &&
    !featuredToursLoading &&
    filter === "All" &&
    // Display only two page numbers at a time
    [...Array(Math.min(pageCount, 2)).keys()].map((index) => {
      const pageNumber = page - 1 + index; // Calculate the actual page number
      return (
        <span
          key={pageNumber}
          onClick={() => setPage(pageNumber)}
          className={page === pageNumber ? "active_page" : ""}
        >
          {pageNumber + 1}
        </span>
      );
    })}
</div>

                </Col>
              </Row>
            )}
          {(toursLoading || tourCountLoading || featuredToursLoading) && ( // Adjusted this line
            <h4 className="text-center pt-5">Loading......</h4>
          )}
          {(toursError || tourCountError || featuredToursError) && ( // Adjusted this line
            <h4 className="text-center pt-5">Failed to fetch data.</h4>
          )}
        </Container>
      </section>
      <Newsletter />
    </>
  );
}
