import React, { useState, useEffect } from 'react';
import { Modal, ModalHeader, ModalBody, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import axios from 'axios';

const EditProfile = ({ isOpen, toggle, user }) => {
  const [userData, setUserData] = useState(user);
  const [photoFile, setPhotoFile] = useState(null);

  useEffect(() => {
    setUserData(user);
  }, [user]);

  useEffect(() => {
    localStorage.setItem('user', JSON.stringify(userData));
  }, [userData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData(prevUserData => ({
      ...prevUserData,
      [name]: value
    }));
  };

  const handlePhotoChange = (e) => {
    setPhotoFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('username', userData.username);
      formData.append('email', userData.email);
      formData.append('phone', userData.phone);
      if (photoFile) {
        formData.append('photo', photoFile);
      }
  
      // Fetch the current authToken from localStorage
      const authToken = localStorage.getItem("authToken");
  
      // Send the form data to update the user
      const response = await axios.put(`http://localhost:7000/api/user/${userData._id}`, formData, {
        headers: {
          Authorization: `Bearer ${authToken}`, // Include the authToken in the request headers
        },
      });
  
      const updatedUser = response.data.user;
  
      // Update the userData state with the updated user data
      setUserData(updatedUser);
  
      // Update the user data in localStorage
      localStorage.setItem('user', JSON.stringify(updatedUser));
      localStorage.s
  
      toggle();
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };
  
  return (
    <Modal isOpen={isOpen} toggle={toggle}>
      <ModalHeader toggle={toggle}>Edit Profile</ModalHeader>
      <ModalBody>
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label for="username">Username</Label>
            <Input type="text" name="username" id="username" value={userData.username} onChange={handleChange} />
          </FormGroup>
          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" name="email" id="email" value={userData.email} onChange={handleChange} />
          </FormGroup>
          <FormGroup>
            <Label for="phone">Phone</Label>
            <Input type="text" name="phone" id="phone" value={userData.phone} onChange={handleChange} />
          </FormGroup>
          {/* Input field for selecting photo file */}
          <FormGroup>
            <Label for="photo">Profile Photo</Label>
            <Input type="file" name="photo" id="photo" onChange={handlePhotoChange} />
          </FormGroup>
          <Button type="submit" color="primary">Update</Button>
        </Form>
      </ModalBody>
    </Modal>
  );
};

export default EditProfile;
