import React, { useState, useEffect } from "react";
import { CommonSection } from "../shared/CommonSection";
import { Container, Row, Col } from "reactstrap";
import { SearchBar } from "../shared/SearchBar";
import { Newsletter } from "../shared/Newsletter";
import TourCard from "../shared/TourCard";
import { useFetch } from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";
import "../styles/tour.css";

export default function Tours() {
  const [pageCount, setPageCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1); // Changed to start from 1

  const { data: tours, loading, error } = useFetch(`${BASE_URL}/tours/get?page=${currentPage}`);
  const { data: tourCount } = useFetch(`${BASE_URL}/tours/search/getTourCount`);

  useEffect(() => {
    if (tourCount) {
      const pages = Math.ceil(tourCount / 8); // Calculate total pages
      setPageCount(pages);
    }
    window.scrollTo(0, 0); // Scroll to top when page or tourCount changes
  }, [currentPage, tourCount]);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <>
      <CommonSection title="All Tours" />
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          {loading && <h4 className="text-center pt-5">Loading...</h4>}
          {error && <h4 className="text-center pt-5">{error}</h4>}
          {!loading && !error && (
            <Row>
              {tours?.map((tour) => (
                <Col lg="3" className="mb-4" key={tour._id}>
                  <TourCard tour={tour} />
                </Col>
              ))}
              <Col lg="12">
                <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                  {[...Array(pageCount).keys()].map((number) => (
                    <span
                      key={number}
                      onClick={() => handlePageChange(number + 1)} // Page numbers start from 1
                      className={currentPage === number + 1 ? "active_page" : ""}
                    >
                      {number + 1}
                    </span>
                  ))}
                </div>
              </Col>
            </Row>
          )}
        </Container>
      </section>
      <Newsletter />
    </>
  );
}
