import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import axios from 'axios'; // Import Axios for making HTTP requests

const defaultTheme = createTheme();

export default function AddTour() {
  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData(event.currentTarget);

    // Create a data object from form fields
    const data = {
      title: formData.get('title'),
      city: formData.get('city'),
      address: formData.get('address'),
      photo: formData.get('photo'),
      desc: formData.get('desc'),
      price: formData.get('price'),
      maxGroupSize: formData.get('maxGroupSize'),
    };

    try {
      // Send a POST request to your server to add the tour
      const response = await axios.post('/api/tours', data);
      console.log('Tour added successfully:', response.data);
      // Handle success: e.g., show a success message to the user
    } catch (error) {
      console.error('Error adding tour:', error.response.data);
      // Handle error: e.g., show an error message to the user
    }
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Add Tour
          </Typography>
          <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="title"
              label="Title"
              name="title"
              autoFocus
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="city"
              label="City"
              name="city"
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="address"
              label="Address"
              name="address"
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="photo"
              label="Photo URL"
              name="photo"
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="desc"
              label="Description"
              name="desc"
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="price"
              label="Price"
              name="price"
              type="number"
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="maxGroupSize"
              label="Max Group Size"
              name="maxGroupSize"
              type="number"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Add Tour
            </Button>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
}
