import React, { useState, useEffect } from "react";
import { CommonSection } from "../shared/CommonSection";
import "../styles/tour.css";
import TourCard from "./../shared/TourCard";
import { Newsletter } from "./../shared/Newsletter";
import { Container, Row, Col, Button } from "reactstrap"; // Import Button from reactstrap
import { SearchBar } from "../shared/SearchBar";
import { useFetch } from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";

export default function Tours() {
  const [pageCount, setpagecount] = useState(0);
  const [page, setPage] = useState(0);
  const [filter, setFilter] = useState("All");

  const {
    data: tours,
    loading: toursLoading,
    error: toursError,
  } = useFetch(`${BASE_URL}/tours/get?page=${page}`);

  const {
    data: tourCount,
    loading: tourCountLoading,
    error: tourCountError,
  } = useFetch(`${BASE_URL}/tours/search/getTourCount`);

  const {
    data: featuredTours,
    loading: featuredToursLoading,
    error: featuredToursError,
  } = useFetch(`${BASE_URL}/tours/search/Featured2`);

  useEffect(() => {
    if (tourCount) {
      const pages = Math.ceil(tourCount / 8);
      setpagecount(pages);
      window.scrollTo(0, 0);
    }
  }, [page, tourCount, tours]);

  console.log("Tours:", tours);
  console.log("Featured Tours:", featuredTours);

  const handleFilterChange = (selectedFilter) => {
    setFilter(selectedFilter);
    setPage(0);
  };

  return (
    <>
      {/* Your existing JSX */}
      {!toursLoading && !tourCountLoading && !featuredToursLoading && !toursError && !tourCountError && !featuredToursError && (
        // Check if data is loaded and no errors occurred
        <Row>
          {filter === "All" ? (
            tours?.map((tour) => (
              <Col lg="3" className="mb-4" key={tour._id}>
                <TourCard tour={tour} />
              </Col>
            ))
          ) : (
            featuredTours?.data?.map((tour) => (
              <Col lg="3" className="mb-4" key={tour._id}>
                <TourCard tour={tour} />
              </Col>
            ))
          )}
          <Col lg="12">
            <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
              {[...Array(pageCount).keys()].map((number) => (
                <span
                  key={number}
                  onClick={() => setPage(number)}
                  className={page === number ? "active_page" : ""}
                >
                  {number + 1}
                </span>
              ))}
            </div>
          </Col>
        </Row>
      )}
      {toursLoading || tourCountLoading || featuredToursLoading && (
        // Show loading indicator
        <h4 className="text-center pt-5">Loading......</h4>
      )}
      {toursError || tourCountError || featuredToursError && (
        // Show error message
        <h4 className="text-center pt-5">Failed to fetch data.</h4>
      )}
    </>
  );
}
