import React, { useRef, useEffect, useContext, useState } from "react";
import { Container, Row, Button, Modal, ModalHeader, ModalBody } from "reactstrap";
import { NavLink, Link, useNavigate } from "react-router-dom";

import logo from "../../assets/images/logo1.jpeg";
import "./header.css";
import { AuthContext } from "./../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

const navLinks = [
  { path: "/home", display: "Home" },
  { path: "/about", display: "About" },
  { path: "/tours", display: "Tours" },
];

export default function Header() {
  const headerRef = useRef(null);
  const navigate = useNavigate();
  const { user, dispatch } = useContext(AuthContext);
  const [modalOpen, setModalOpen] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState([]);

  const logout = () => {
    dispatch({ type: "LOGOUT" });
    navigate("/");
  };

  const handleScroll = () => {
    if (window.scrollY > 80) {
      headerRef.current.classList.add("sticky_header");
    } else {
      headerRef.current.classList.remove("sticky_header");
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const toggleModal = () => {
    setModalOpen(!modalOpen);
  };

  const handleNotificationClick = async () => {
    try {
      const authToken = localStorage.getItem("authToken");
      if (!authToken) {
        throw new Error("User not logged in");
      }
  
      const res = await fetch(`${BASE_URL}/booking/user`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${authToken}`,
        },
        credentials: "include",
      });
  
      if (!res.ok) {
        throw new Error("Failed to fetch notifications");
      }
  
      const result = await res.json();
      if (Array.isArray(result.data)) {
        setNotificationMessage(result.data);
        setModalOpen(true);
      } else {
        throw new Error("Invalid data format in the response");
      }
    } catch (err) {
      console.error(err);
      // Handle error state or show a notification to the user
    }
  };

  return (
    <header className="header" ref={headerRef}>
      <Container>
        <Row>
          <div className="nav_wrapper d-flex align-items-center justify-content-between">
            <div className="logo">
              <img src={logo} alt="Logo" />
            </div>
            <div className="navigation">
              <ul className="menu d-flex align-items-center gap-5">
                {navLinks.map((item, index) => (
                  <li className="nav_item" key={index}>
                    <NavLink to={item.path}>{item.display}</NavLink>
                  </li>
                ))}
              </ul>
            </div>
            <div className="nav_right d-flex align-items-center gap-4">
              <div className="notification_bell" onClick={handleNotificationClick}>
                <i className="ri-notification-2-line"></i>
              </div>
              <div className="nav_btns d-flex align-items-center gap-4">
                {user ? (
                  <>
                    <h5 className="mb-0">
                      <img
                        src={`http://localhost:7000/uploads/users/${user.photo}`}
                        alt="User"
                        style={{
                          height: "45px",
                          width: "50px",
                          borderRadius: "50%",
                        }}
                      />
                      <span style={{ marginRight: "15px" }} />
                      {user.username}
                    </h5>
                    <Button className="btn primary_btn" onClick={logout}>
                      Logout
                    </Button>
                  </>
                ) : (
                  <>
                    <Button className="btn secondary_btn">
                      <Link to="/login">Login</Link>
                    </Button>
                    <Button className="btn primary_btn">
                      <Link to="/register">Register</Link>
                    </Button>
                  </>
                )}
              </div>
              <span className="mobile_menu">
                <i className="ri-menu-line"></i>
              </span>
            </div>
          </div>
        </Row>
      </Container>
      <Modal isOpen={modalOpen} toggle={toggleModal}>
        <ModalHeader toggle={toggleModal}>Notification</ModalHeader>
        <ModalBody>
          {notificationMessage.map((item, index) => (
            <div key={index}>
              <h6>Order ID: {item.orderId}</h6>
              <p>Message: {item.message}</p>
            </div>
          ))}
        </ModalBody>
      </Modal>
    </header>
  );
}
