import React, { useRef, useState, useEffect } from "react";
import "../styles/tour-details.css";
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import calculationAvgRating from "../utils/avgRating";
import avatar from "../assets/images/avatar.jpg";
import { Booking } from "../components/Booking/Booking";
import { Newsletter } from "../shared/Newsletter";
import axios from "axios";

export const TourDetails = () => {
  const [tourData, setTourData] = useState(null); // Change initial state to null

  useEffect(() => {
    axios
      .get("http://localhost:7000/api/tour/get/" + id) // Use id from useParams
      .then((response) => {
        setTourData(response.data.tour); // Set data to tour instead of tours
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const { id } = useParams();
  const reviewMsgRef = useRef("");
  const [tourRating, setTourRating] = useState(null);

  const options = { day: "numeric", month: "long", year: "numeric" };

  const submitHandler = (e) => {
    e.preventDefault();
    const reviewText = reviewMsgRef.current.value;
    alert(`${reviewText}, ${tourRating}`); // Fixed typo and added space
  };

  if (!tourData) return null; // Render nothing if tourData is null

  const { totalRating, avgRating } = calculationAvgRating(tourData.reviews);

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="tour_content">
                <img
                  src={`http://localhost:7000/uploads/tour/${tourData.photo}`}
                  alt=""
                />
                <div className="tour_info">
                  <h2>{tourData.title}</h2>
                  <div className="d-flex align-items-center gap-5">
                    <span className="tour_rating d-flex align-items-center gap-1">
                      <i
                        className="ri-star-fill"
                        style={{ color: "var(--secondary-color)" }}
                      ></i>
                      {avgRating === 0 ? null : avgRating}
                      {totalRating === 0 ? (
                        "Not Rated"
                      ) : (
                        <span>({tourData.reviews.length})</span>
                      )}
                    </span>

                    <span>
                      <i className="ri-map-pin-user-fill"></i> {tourData.address}
                    </span>
                  </div>
                  <div className="tour_extra-details">
                    <span>
                      <i className="ri-map-pin-2-line"></i>
                      {tourData.city}
                    </span>
                    <span>
                      <i className="ri-money-rupee-circle-fill"></i>
                      Rs{tourData.price}/per person
                    </span>
                    <span>
                      <i className="ri-group-line"></i>
                      {tourData.maxGroupSize} people
                    </span>
                  </div>
                  <h5>Description</h5>
                  <p>{tourData.desc}</p>
                </div>
                {/* tour review section */}
                <div className="tour_reviews mt-4">
                  <h4>Reviews({tourData.reviews.length} reviews)</h4>

                  <Form onSubmit={submitHandler}>
                    <div className="d-flex align-items-center gap-3 mb-4 rating_group">
                      {[1, 2, 3, 4, 5].map((rating) => ( // Map over an array to generate stars
                        <span key={rating} onClick={() => setTourRating(rating)}>
                          {rating} <i className="ri-star-fill"></i>
                        </span>
                      ))}
                    </div>
                    <div className="review_input">
                      <input
                        type="text"
                        ref={reviewMsgRef}
                        placeholder="Share your thoughts"
                        required
                      />
                      <button
                        className="btn primary_btn text-white"
                        type="submit"
                      >
                        Submit
                      </button>
                    </div>
                  </Form>

                  <ListGroup className="user_reviews">
                    {tourData.reviews.map((review, index) => (
                      <div key={index} className="review_item"> {/* Add key to review_item */}
                        <img src={avatar} alt="" />
                        <div className="w-100">
                          <div className="d-flex align-items-center justify-content-between">
                            <div>
                              <h5>{review.author}</h5> {/* Use review.author for author name */}
                              <p>
                                {new Date(review.date).toLocaleDateString( // Use review.date for review date
                                  "en-US",
                                  options
                                )}
                              </p>
                            </div>
                            <span className="d-flex align-items-center">
                              {review.rating} <i className="ri-star-fill"></i> {/* Use review.rating for star rating */}
                            </span>
                          </div>
                          <h6>{review.comment}</h6> {/* Use review.comment for review comment */}
                        </div>
                      </div>
                    ))}
                  </ListGroup>
                </div>
                {/* tour review section end */}
              </div>
            </Col>

            <Col lg="4">
              <Booking tour={tourData} avgRating={avgRating} /> {/* Pass tourData instead of tour */}
            </Col>
          </Row>
        </Container>
      </section>
      <Newsletter />
    </>
  );
};
