import React, { useEffect, useState } from "react";
import { Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import "./tour-card.css";
import calculationAvgRating from "../utils/avgRating";
import axios from "axios";


const TourCard = () => {
  const [TourCard, setTourCard] = useState([]);


  useEffect(() => {
    let token = JSON.parse(localStorage.getItem("token"));
    axios
      .get("http://localhost:7000/api/tour/get")
      .then((response) => {
        console.log(response.data);
        setTourCard(response.data)
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);
  console.log(TourCard);

  

  return (
    <div className="tour_card">
      {TourCard.map((item,index)=>(
      <Card>
        <div className="tour_img">
          <img src={`http://localhost:7000/uploads/tour/${item.photo}`} alt="tour-img" />
          {item.featured && <span>Featured</span>}
        </div>

        <CardBody>
          <div className="card_top d-flex align-items-center justify-content-between">
            <span className="tour_loaction d-flex align-items-center gap-1">
              <i className="ri-map-pin-line"></i>
              {item.city}
            </span>
            <span className="tour_rating d-flex align-items-center gap-1">
              <i className="ri-star-fill"></i>
              {avgRating === 0 ? null : avgRating}
              {totalRating === 0 ? (
                "Not Rated"
              ) : (
                <span>({item.reviews.length})</span>
              )}
            </span>
          </div>
          <h5 className="tour_title">
            <Link to={`/tours/${item.id}`}>{item.title}</Link>
          </h5>
          <h5>
            <div className="card_button d-flex align-items-center justify-content-between mt-3">
              <h5>
                {item.price}Rs
                <span>/per person</span>
              </h5>
              <button className="btn booking_btn">
                <Link to={`/tours/${item.id}`}>Book Now</Link>
              </button>
            </div>
          </h5>
        </CardBody>
      </Card>
      ))}
    </div>
  );
};

export default TourCard;
