import React from "react";
import "./booking..css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";

export const Booking = ({ tour, avgRating }) => {
  const { price, reviews } = tour;
  return (
    <div className="booking">
      <div className="booking_top d-flex align-items-center justify-content-between">
        <h3>
          Rs{price} <span>/per person</span>
        </h3>
        <span className="tour_rating d-flex align-items-center">
          <i class="ri-star-fill"></i>
          {avgRating === 0 ? null : avgRating} ({reviews?.length})
        </span>
      </div>

      {/* Booking form */}
      <div className="booking_form">
        <h5>Information</h5>
        <Form className="booking_info-form">
            <FormGroup>
                <input type="text" placeholder="Full Name" id="fullName" required onChange={handleChange}/>
            </FormGroup>
            <FormGroup>
                <input type="number" placeholder="FullName"  id=""required onChange={handleChange}/>
            </FormGroup> <FormGroup>
                <input type="text" placeholder="FullName" required onChange={handleChange}/>
            </FormGroup>
           
        </Form>
      </div>
      {/* Booking form end */}
    </div>
  );
};
