import React, { createContext, useEffect, useReducer } from "react";

let user;
try {
  user = JSON.parse(localStorage.getItem("user"));
} catch (error) {
  console.error("Error parsing user data from localStorage:", error);
  user = null;
}
let token;
try {
  to = JSON.parse(localStorage.getItem("authToken"));
} catch (error) {
  console.error("Error parsing user data from localStorage:", error);
  user = null;
}

const initial_state = {
  user: user,
  token:token,
  loading: false,
  error: null,
};

export const AuthContext = createContext(initial_state);

const AuthReducer = (state, action) => {
  switch (action.type) {
    case "LOGIN_START":
      return {
        user: null,
        loading: false,
        error: null,
      };
    case "LOGIN_SUCCESS":
      return {
        user: action.payload,
        loading: false,
        error: null,
      };

    case "LOGIN_FAILURE":
      return {
        user: null,
        loading: false,
        error: action.payload,
      };

    case "REGISER_SUCCESS":
      return {
        user: null,
        loading: false,
        error: null,
      };
    case "LOGOUT":
      return {
        user: null,
        loading: false,
        error: null,
      };

    default:
      return state;
  }
};

export const AuthContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AuthReducer, initial_state);

  useEffect(() => {
    localStorage.setItem("user", JSON.stringify(state.user));
  }, [state.user]);
  useEffect(() => {
    localStorage.setItem("authToken", JSON.stringify(state.token));
  }, [state.token]);

  useEffect(() => {
    if (state.user && state.user.status === "blocked") {
      // Clear user and auth token from local storage
      localStorage.removeItem("user");
      localStorage.removeItem("authToken");
      dispatch({ type: "LOGOUT" });
    }
  }, [state.user]);

  return (
    <AuthContext.Provider
      value={{
        user: state.user,
        loading: state.loading,
        error: state.error,
        dispatch,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
